  return (
    <form className='todo-form' onSubmit={handleSubmit}>
        <input 
          type='text' 
          placeholder='Add a todo' 
          value={input} name='text' 
          className='todo-input' 
          onChange={handleChange}
        />
        <button className='todo-button'>Add todo</button>

    </form>
  )
}

export default TodoForm
